    public String getRoleForParent(Class c){
    	
    	if(Composit.class.isAssignableFrom(c)){
    	   return "Group_of_components";
    	}
    	
    	if(DependencyModel.class.isAssignableFrom(c)){
    	   return "Comps";
    	}
    	
    	return super.getRoleForParent(c);
    }	
    
